if (!scenario || Object.keys(scenario).length === 0) {
    return 0;
  }

  const { type, purchasePrice, monthlyExpenses, propertyTax, insuranceCosts } = scenario;

  if (!purchasePrice || purchasePrice <= 0) {
    return 0;
  }

  // Calculate annual fixed expenses
  const annualFixedExpenses = (monthlyExpenses || 0) * 12 + (propertyTax || 0) + (insuranceCosts || 0);

  if (type === 'resale') {
    const { salePrice, holdingPeriod, renovationCosts, sellingCosts } = scenario;
    if (!salePrice || !holdingPeriod || holdingPeriod <= 0) {
      return 0;
    }

    // Total expenses over holding period
    const totalExpenses = annualFixedExpenses * holdingPeriod + (renovationCosts || 0) + (sellingCosts || 0);

    // Net profit
    const netProfit = salePrice - purchasePrice - totalExpenses;

    // Annual net yield
    return ((netProfit / holdingPeriod) / purchasePrice) * 100;

  } else if (type === 'long_term_rental') {
    const { monthlyRent, vacancyRate, managementFees, renovationCosts } = scenario;
    if (!monthlyRent) {
      return 0;
    }

    // Annual gross income with vacancy rate
    const effectiveOccupancyRate = 1 - (vacancyRate / 100);
    const annualRent = monthlyRent * 12 * effectiveOccupancyRate;

    // Management fees
    const managementCosts = annualRent * ((managementFees || 0) / 100);

    // Amortized renovation costs (over 10 years)
    const annualRenovationCosts = (renovationCosts || 0) / 10;

    // Total annual expenses
    const totalAnnualExpenses = annualFixedExpenses + managementCosts + annualRenovationCosts;

    // Net annual income
    const netAnnualIncome = annualRent - totalAnnualExpenses;

    return (netAnnualIncome / purchasePrice) * 100;

  } else if (type === 'nightly_rental') {
    const { nightlyRate, occupancyRate, managementFees, cleaningCosts, renovationCosts } = scenario;
    if (!nightlyRate || !occupancyRate) {
      return 0;
    }

    // Annual gross income with occupancy rate
    const daysOccupied = 365 * (occupancyRate / 100);
    const annualIncome = nightlyRate * daysOccupied;

    // Management fees
    const managementCosts = annualIncome * ((managementFees || 0) / 100);

    // Cleaning costs
    // Assume average stay is 3 days
    const numberOfStays = daysOccupied / 3;
    const annualCleaningCosts = (cleaningCosts || 0) * numberOfStays;

    // Amortized renovation costs (over 5 years for nightly rental due to higher wear)
    const annualRenovationCosts = (renovationCosts || 0) / 5;

    // Total annual expenses
    const totalAnnualExpenses = annualFixedExpenses + managementCosts + annualCleaningCosts + annualRenovationCosts;

    // Net annual income
    const netAnnualIncome = annualIncome - totalAnnualExpenses;

    return (netAnnualIncome / purchasePrice) * 100;
