// Get the front document and window
  const doc = wwLib.getFrontDocument();
  const win = wwLib.getFrontWindow();

  if (!doc || !win) return;

  // Get the hero elements
  const heroImage = doc.getElementById('9d89c092-a957-42c9-8235-3e5e9bc904db');
  const circle1 = doc.getElementById('circle_1');
  const circle2 = doc.getElementById('circle_2');
  const square1 = doc.getElementById('square_1');
  const square2 = doc.getElementById('square_2');
  const connectingLine = doc.getElementById('connecting_line');

  if (!heroImage) return;

  // Define the parallax effect parameters with different speeds for each element
  const parallaxConfig = {
    heroImage: { speed: 0.05, limit: 20 },
    circle1: { speed: 0.08, limit: 30 },
    circle2: { speed: -0.06, limit: 25 },
    square1: { speed: 0.1, limit: 35 },
    square2: { speed: -0.07, limit: 28 },
    connectingLine: { speed: 0.03, limit: 15 }
  };

  // Add scroll event listener
  const handleScroll = () => {
    const scrollTop = win.scrollY;

    // Apply parallax to hero image
    if (heroImage) {
      const translateY = Math.min(scrollTop * parallaxConfig.heroImage.speed, parallaxConfig.heroImage.limit);
      heroImage.style.transform = `translateY(${translateY}px)`;
    }

    // Apply parallax to decorative elements
    if (circle1) {
      const translateY = Math.min(scrollTop * parallaxConfig.circle1.speed, parallaxConfig.circle1.limit);
      const currentTransform = circle1.style.transform || '';
      circle1.style.transform = `translateY(${translateY}px)`;
    }

    if (circle2) {
      const translateY = Math.min(scrollTop * parallaxConfig.circle2.speed, parallaxConfig.circle2.limit);
      circle2.style.transform = `translateY(${translateY}px)`;
    }

    if (square1) {
      const translateY = Math.min(scrollTop * parallaxConfig.square1.speed, parallaxConfig.square1.limit);
      square1.style.transform = `rotate(45deg) translateY(${translateY}px)`;
    }

    if (square2) {
      const translateY = Math.min(scrollTop * parallaxConfig.square2.speed, parallaxConfig.square2.limit);
      square2.style.transform = `rotate(30deg) translateY(${translateY}px)`;
    }

    if (connectingLine) {
      const translateY = Math.min(scrollTop * parallaxConfig.connectingLine.speed, parallaxConfig.connectingLine.limit);
      connectingLine.style.transform = `translateY(${translateY}px)`;
    }
  };

  // Add mouse move parallax effect
  const handleMouseMove = (event) => {
    const { clientX, clientY } = event;
    const windowWidth = win.innerWidth;
    const windowHeight = win.innerHeight;

    // Calculate mouse position relative to the center of the window
    const mouseXRatio = (clientX - windowWidth / 2) / (windowWidth / 2);
    const mouseYRatio = (clientY - windowHeight / 2) / (windowHeight / 2);

    // Apply subtle mouse-based parallax to decorative elements
    if (circle1) {
      circle1.style.transform = `translate(${mouseXRatio * 10}px, ${mouseYRatio * 10}px)`;
    }

    if (circle2) {
      circle2.style.transform = `translate(${mouseXRatio * -8}px, ${mouseYRatio * -8}px)`;
    }

    if (square1) {
      square1.style.transform = `rotate(45deg) translate(${mouseXRatio * 15}px, ${mouseYRatio * 15}px)`;
    }

    if (square2) {
      square2.style.transform = `rotate(30deg) translate(${mouseXRatio * -12}px, ${mouseYRatio * -12}px)`;
    }
  };

  // Add event listeners
  win.addEventListener('scroll', handleScroll);
  win.addEventListener('mousemove', handleMouseMove);

  // Return a cleanup function
  return () => {
    win.removeEventListener('scroll', handleScroll);
    win.removeEventListener('mousemove', handleMouseMove);
  };
