  const approverEntry = {
    id: currentUser.id,
    name: currentUser.name,
    status: context.parameters['approved'] ? 'approved' : 'rejected',
    date: new Date().toISOString().split('T')[0],
    comments: context.parameters['comments']
  };

  // Check if the user is already in the approvers list
  const approverIndex = invoices[index].approvers.findIndex(a => a.id === currentUser.id);

  if (approverIndex !== -1) {
    // Update existing approver entry
    invoices[index].approvers[approverIndex] = approverEntry;
