  // Get current invoices
  const invoices = [...variables['5201d60e-4f75-4983-880f-669c7c83bf9e']];

  if (mode === 'create') {
    // Generate a new ID
    const newId = (Math.max(...invoices.map(invoice => parseInt(invoice.id) || 0), 0) + 1).toString();

    // Create new invoice with default status and empty approvers if not provided
    const newInvoice = {
      ...formData,
      id: newId,
      status: formData.status || 'pending',
      approvers: formData.approvers || []
    };

    // Add to invoices
    invoices.push(newInvoice);

    // Update global variable
    variables['5201d60e-4f75-4983-880f-669c7c83bf9e'] = invoices;

  } else if (mode === 'edit') {
    // Find invoice index
    const index = invoices.findIndex(invoice => invoice.id === invoiceId);

    if (index !== -1) {
      // Update invoice
      invoices[index] = {
        ...invoices[index],
        ...formData,
        id: invoiceId
      };

      // Update global variable
      variables['5201d60e-4f75-4983-880f-669c7c83bf9e'] = invoices;

      // Update selected invoice if it's the current one
      if (variables['ec6c3933-bb2d-4e89-a279-2b4f60c57e5a']?.id === invoiceId) {
        variables['ec6c3933-bb2d-4e89-a279-2b4f60c57e5a'] = invoices[index];
      }
    }
  }

